int mandel(
    float c_re, 
    float c_im, 
    int max_iter)
{
  int i = 0;
  float temp=0.0, z_re=0.0, z_im=0.0;
  while ( ( ((z_re*z_re)+(z_im*z_im)) < 4.0 ) && ( i < max_iter ) )
  {
    temp =(z_re*z_re)-(z_im*z_im)+c_re;
    z_im = 2.0*(z_re*z_im)+c_im;
    z_re=temp;
    i++;
  }
  return i;
}

void apply_mandel(
    int** quad_mx, 
    int re_size, 
    int im_size,
    float re_lo, 
    float re_hi, 
    float im_lo, 
    float im_hi, 
    int max_iter)
{

  int re_step,im_step,itercount;
  float d_re,d_im,c_re,c_im;
  if(quad_mx == NULL || re_size < 1 || im_size < 1) return;
  d_re = (re_hi-re_lo)/re_size;
  d_im = (im_hi-im_lo)/im_size;
  for(re_step = 0; re_step < re_size;++re_step){
    c_re = re_lo + d_re * re_step;
    for(im_step = 0; im_step < im_size;++im_step){
      c_im = im_hi - d_im * im_step;
      itercount = mandel( c_re,c_im,max_iter);
      quad_mx[re_step][im_step]=mandel( c_re,c_im,max_iter);
    }
  }
}

int w; // scrren width
int h; // screen height
int iter_lim; // iteration limit for mandel
double a; // 
double b;
double c;
double d;

void render(SDL_Renderer *renderer)
{
  int szazalek = 0; // unused
  double dx = ( b - a ) / w;
  double dy = ( d - c ) / h;
  double reC, imC, reZ, imZ;
  int iteracio = 0;
  for ( int j = 0; j < h; ++j ){
    imC = d - j * dy;
    for ( int k = 0; k < w; ++k ){
      reC = a + k * dx;
      std::complex<double> c ( reC, imC );
      std::complex<double> z_n ( 0, 0 );
      iteracio = 0;
      while ( std::abs ( z_n ) < 4 && iteracio < iter_lim ){
        z_n = z_n * z_n + c;
        ++iteracio;
      }
      SDL_SetRenderDrawColor(renderer, iteracio%255, (iteracio*iteracio)%255,0, 0xff);
      SDL_RenderDrawPoint(renderer, k, j);
    }
    szazalek = ( double ) j / ( double ) h * 100.0;
  }
}

std::complex<double> cc ( reC, imC );
double dx = ( xmax - xmin ) / w;
double dy = ( ymax - ymin ) / h;
for ( int y = 0; y < h; ++y ){
    for ( int x = 0; x < w; ++x ){
        double reZ = xmin + x * dx;
        double imZ = ymax - y * dy;
        std::complex<double> z_n ( reZ, imZ );
        int iteracio = 0;
        for (int i=0; i < iter_lim; ++i){
            z_n = std::pow(z_n, 2) + cc;
            if( std::abs(z_n) > R){
                iteracio = i;
                break;
            }
        }
        SDL_SetRenderDrawColor(renderer, (iteracio * 20)%255, (iteracio* 40)%255, (iteracio* 60)%255 , 0xff);
        SDL_RenderDrawPoint(renderer, x,y);
    }
}
