	protected Node cp(Node node, Node treep) {
		Node newNode = null;

		if (node != null) {
			newNode = new Node(node.val);
			newNode.left = cp(node.left, treep);
			newNode.right = cp(node.right, treep);
			newNode.count = node.count;
			if (node == treep)
				this.treep = newNode;
		}

		return newNode;
	}
